import { Component, OnInit } from '@angular/core';
import { CrimesService } from './crimes.service';
import { HttpClient } from '@angular/common/http';  // Importa HttpClient
import * as L from 'leaflet';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  crimes: any[] = []; // Array per memorizzare i crimini

  constructor(private crimesService: CrimesService) { }


  map!: L.Map;
  ngOnInit(): void {
    this.initMap();
  }

  // Function to initialize the map
  initMap(): void {
    this.map = L.map('map', {
      center: [41.816813771373916, -87.60670812560372],
      zoom: 14
    });
    getCrimes
    // Set the OpenStreetMap tile layer
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(this.map);

    // Add GeoJSON layer
    L.geoJSON(this.geoJsonData).addTo(this.map);
  }
